<h1>Form - Checkboxes and Radios</h1>
<fieldset>
    <legend>
        Legend - Checkboxes Fields:
    </legend>
    <div class="form-group">
        <input id="input-checkbox" name="input-checkbox" type="checkbox" value="1" />
        <label for="input-checkbox">Input checkbox</label>
    </div>
    <div class="form-group">
        <input id="input-checkbox-2" name="input-checkbox-2" type="checkbox" value="1" />
        <label for="input-checkbox-2">Input checkbox 2</label>
    </div>
    <div class="form-group">
        <input id="input-checkbox-3" name="input-checkbox-3" type="checkbox" value="1" />
        <label for="input-checkbox-3">Input checkbox 3</label>
    </div>
</fieldset>
<fieldset>
    <legend>
        Legend - Radios Fields:
    </legend>
    <div class="form-group">
        <input id="input-radio" name="input-radio" type="radio" value="1" />
        <label for="input-radio">Input radio</label>
    </div>
    <div class="form-group">
        <input id="input-radio-2" name="input-radio-2" type="radio" value="1" />
        <label for="input-radio-2">Input radio 2</label>
    </div>
    <div class="form-group">
        <input id="input-radio-3" name="input-radio-3" type="radio" value="1" />
        <label for="input-radio-3">Input radio 3</label>
    </div>
</fieldset>
