<h1>Form - Range Fields</h1>
<fieldset>
    <legend>
        Legend - Range Fields:
    </legend>
    <div class="form-group">
        <label for="range">Range</label>
        <input id="range" name="range" type="range" value="50" />
    </div>

    <div class="form-group">
        <label for="range">Range Disabled</label>
        <input id="range" name="range" type="range" value="50" disabled/>
    </div>
</fieldset>
