<h1>Form - Selects</h1>
<fieldset>
    <legend>
        Legend - Select Fields:
    </legend>
    <div class="form-group">
        <label for="select">Select</label>
        <select id="select" name="select">
            <option value="opt-1">Option 1</option>
            <option value="opt-2">Option 2</option>
            <option value="opt-3">Option 3</option>
            <option value="opt-4">Option 4</option>
            <option value="opt-5">Option 5</option>
        </select>
    </div>
    <div class="form-group">
        <label for="select-optgroup">Select with optgroup</label>
        <select id="select-optgroup" name="select-optgroup">
            <optgroup label="Option group 1">
                <option value="opt-1">Option 1</option>
                <option value="opt-2">Option 2</option>
            </optgroup>
            <optgroup label="Option group 2">
                <option value="opt-3">Option 3</option>
                <option value="opt-4">Option 4</option>
            </optgroup>
        </select>
    </div>
    <div class="form-group">
        <label for="select-multiple">Select Multiple</label>
        <select id="select-multiple" name="select" multiple="multiple">
            <optgroup label="Option group 1">
                <option value="opt-1">Option 1</option>
                <option value="opt-2">Option 2</option>
            </optgroup>
            <optgroup label="Option group 2">
                <option value="opt-3">Option 3</option>
                <option value="opt-4">Option 4</option>
            </optgroup>
        </select>
    </div>
    <div class="form-group">
        <label for="select">Select Disabled</label>
        <select id="select" name="select" disabled>
            <option value="opt-1">Option 1</option>
            <option value="opt-2">Option 2</option>
            <option value="opt-3">Option 3</option>
            <option value="opt-4">Option 4</option>
            <option value="opt-5">Option 5</option>
        </select>
    </div>
</fieldset>
